providers.map:
function(doc) {
  var key = {};
  key['providerId'] = doc.providerId;
  if(doc.method) {
    key['method'] = doc.method;
  } else {
    key['type'] = doc.type;
  }
  emit(key, doc);
}

feedback.map:
function(doc) {
  if(doc.element) {
    var key = {};
    key['providerId'] = doc.providerId;
    key['element'] = doc.element;

    emit(key, doc);
  }
}

comments.map:
function(doc) {
  if(doc.comments) {
    for(var i = 0; i < doc.comments.length; i++) {
      var entry = {};
      entry['date'] = doc.comments[i].date;
      entry['text'] = doc.comments[i].text;
      entry['username'] = doc.comments[i].username;
      entry['providerId'] = doc.providerId;
      entry['element'] = doc.element;
      emit(entry.date, entry);
    }
  }
}

ratings.map:
function(doc) {
  if(doc.comments) {
    for(var i = 0; i < doc.ratings.length; i++) {
      var entry = {};
      entry['date'] = doc.ratings[i].date;
      entry['rating'] = doc.ratings[i].rating;
      entry['providerId'] = doc.providerId;
      entry['element'] = doc.element;
      emit(entry.date, entry);
    }
  }
}
