providers.map:
function(doc) {
  key = {};
  key["providerId"] = doc.providerId;
  key["type"] = doc.type;
  key["method"] = doc.method;
  emit(key, doc);
}

feedback.map:
function(doc) {
  if(doc.element) {
    var key = {};
    key['providerId'] = doc.providerId;
    key['element'] = doc.element;
    emit(key, doc);
  }
}
