modelSpecificationsIncludeAlias.map:
function(doc) {
	if(!doc.symbolicName || !doc.versionRange)
		return;

	emit(doc.symbolicName, doc);

	if(doc.aliases) {
		for(var i=0; i<doc.aliases.length; i++) {
			emit(doc.aliases[i], doc);
		}
	}
}

modelSpecificationByFingerprint.map:
function(doc) {
	if(!doc.symbolicName || !doc.versionRange || !doc.fingerprints)
		return;

	for(var i=0; i<doc.fingerprints.length; i++) {
		emit(doc.fingerprints[i], doc);
	}
}
